import React, { useState } from 'react'
import './App.css'
import Header from './Header/Header'
import Note from './Note/Note'
import MiniNote from './MiniNote/MiniNote'

const App = () => {
  
  const[addIteam, setAddIteam] =useState([]);

  const addNote = (note) => {
    setAddIteam((prevData)=>{
      return [...prevData, note]
    });
      console.log(note);
  }

  const onDelete = (id) => {
    setAddIteam((oldData) => 
    oldData.filter((currData, index) => {
      return index !== id;
    }))
  }

  return (
    <>
      <Header/>
      <Note passNote={addNote}/>
      {addIteam.map((val, index) => {
        return <MiniNote 
        key={index}
        id={index}
        title={val.title}
        content={val.content}
        deleteIteam={onDelete}/>
      })};
    </>
  )
}

export default App
