import React from 'react'
import { useState } from 'react'
import './Note.css'

const Note = (props) => {
   
  const[note, setNote] = useState({
    title: "",
    content: "",
  });

  const inputEvent = (event) => {
    const{name, value} = event.target;

    setNote((prevData) => {
      return{
        ...prevData,[name]:value,
      };
    })
    console.log(note);
  }

  const addNote = () => {
      props.passNote(note);
      setNote({
        title: "",
        content: "",
      });
  }


  return (
    <>
      <div className='Note'>
        <div className='box'>
        <div className='title_box flex1'>
            <input type="text" 
            name='title' 
            id='title' 
            placeholder='title' 
            value={note.title}
            onChange={inputEvent} 
            autoComplete='off' />
            <button className='add' onClick={addNote}><i class="fa fa-plus-circle" aria-hidden="true"></i></button>
        </div>
        <div className='content_box'>
            <textarea type="text" 
            name='content' 
            id='content' 
            value={note.content}
            onChange={inputEvent} 
            placeholder='Write Anything...' />
        </div>
        </div>
      </div>
    </>
  )
}

export default Note
