import React from 'react'
import './App.css';
import Card from './card';
import books from './data';
//import reportWebVitals from './reportWebVitals';

const App = () => {

    function allcards(val){
        return(
          <Card
              img={val.image}
              title={val.title}
              author={val.author}
              link={val.website}
            />
        )
      }


  return (
    <>
      <div className='header'>
        <h1>Books Store</h1>
      </div>
      
       <div className='booklet'>
       {books.map(allcards)}
       </div>
    </>
  )
}

export default App;
